$('#linkloadmore').on('click',function(event){event.preventDefault();var elload=$("#Blog1_pagination-old");var link=elload.attr('href');var alreadyClicked=elload.data('clicked');if(alreadyClicked){return false;} elload.data('clicked',true);$('#linkloadmore a span').html('Loading...');$.get(link,function(data){var source=$(data).find('.blog-posts').length?$(data):$('<div></div>');$(".blog-posts").append(source.find('.blog-posts').html());var clone=source.find('#Blog1_pagination-old').clone();if(clone.html()===undefined){$(".linkloadmore").html('<div class="alert alert-warning"><i class="fa fa-info-circle fa-fw"/>Empty Post</div>');}else{$("#linkloadmore").html(clone);} elload.data('clicked',false);},"html");})

$(function(){var $goTop=$('<div id="goTop"><img title="Back to Top" src="https://2.bp.blogspot.com/-Hjagi6FjUEs/Wx9cMCctoJI/AAAAAAAAAjA/S59iPJia7OorCVnGzKCAuo6GgD7L7NQ0ACLcBGAs/s1600/icontop.png" alt="Icon backtotop"/></div>').appendTo('body');$(window).scroll(function(){if($(this).scrollTop()!=0){$goTop.fadeIn()}else{$goTop.fadeOut()}});$goTop.click(function(){$('body, html').animate({scrollTop:0},300)})});
