function APIuseJSON(url){				
$('#modal-ReadArticle').modal('show');
var loading = "<img style='width:50px;margin:auto' src='https://1.bp.blogspot.com/-BVhntqN7bBM/V7dtVi3C0zI/AAAAAAAAmho/RrhY5vS_AX062VYgti9KH1kOX4uNPQRkQCPcB/s1600/loading.gif'/>";	
$('#ReadArticle-content').html(loading);
$('#ReadArticle-title').html('Loading...');
$('#ReadArticle-content').load(url + ' .post-body',function(data){	
if (data == undefined) {
$('#ReadArticle-content').html('Error, Click <a href="'+url+'">Here</a> For View');
$('#ReadArticle-title').html('Oops..');
}else {							
var title = $(data).find('.post-title-Content').text();	
$('#ReadArticle-title').html(title);
var names = $('[data-url]').map(function(event,element){
var PostID = $(element).data('url');
$(this).attr('onclick',"APIuseJSON('"+PostID+"')");
}).get();
}
});
}

