safeurlencode = function () {
var safeurlencode = $("safeurlencode").value;
var safeurlencodedomain = extractHostname(safeurlencode);
var safeurlencodepassword = $("safeurlencodepassword").value ? $("safeurlencodepassword").value : "secret";    
var randindex = Math.random() * links.length; 
randindex = parseInt(randindex);
var urlRegex = /(((https?:\/\/)|(www\.))[^\s]+)/g;
if (urlRegex.test(safeurlencode) == false) {
$("safeurlencodesuccess").value = "Website URL is not valid yet";
$("copytoclipboard").style.display = "none";
}else {      
var safeurlencodetitle = $("safeurlencodetitle");			
if (safeurlencodetitle && safeurlencodetitle.value) {
var link = delparm(links[randindex]) + "?safeurl=" + aesCrypto.encrypt(convertstr(safeurlencode),convertstr(safeurlencodepassword)) + "&t=" + aesCrypto.encrypt(convertstr(safeurlencodetitle.value),convertstr("secret"))  + "&d=" + aesCrypto.encrypt(convertstr(safeurlencodedomain),convertstr("secret"));
}else {				
var link = delparm(links[randindex]) + "?safeurl=" + aesCrypto.encrypt(convertstr(safeurlencode),convertstr(safeurlencodepassword))  + "&d=" + aesCrypto.encrypt(convertstr(safeurlencodedomain),convertstr("secret"));
}
$("safeurlencodesuccess").value = link;
$("copytoclipboard").style.display = "block";
$("copytoclipboard").innerHTML = "Copy URL";
}
}

function safeurldecode(){ 
var getdecodeurl = getparm('safeurl');
if ($("checkpassword").innerHTML == 0) {
var safeurldecodepassword = $("safeurldecodepassword").value;
if (safeurldecodepassword.length == 0) {        
$("safeurldecodepassword").placeholder = "Required Password";
$("safeurldecodepassword").select();
return false;
}else {
var safeurldecodepassword = safeurldecodepassword;   
} 
}else {
var safeurldecodepassword = "secret";    
}
var safeurldecode = aesCrypto.decrypt(convertstr(getdecodeurl),convertstr(safeurldecodepassword));
if (safeurldecode == 0) {
$("safeurldecodepassword").value = null;
$("safeurldecodepassword").placeholder = "Wrong Password";
$("safeurldecodepassword").select();
return false;
}else {
window.open(safeurldecode);
}
}		
