var safeurldecode = aesCrypto.decrypt(convertstr(getdecodeurl),convertstr(safeurldecodepassword));   
var safeurldecodecheck = safeurldecode.length; 
$("checkpassword").innerHTML = safeurldecodecheck;		


function timeloader(){
var timeleft = 10000;
var delta = 100;
var downloadTimer = setInterval(function(){
if (window.blurred) {return false;}  
timeleft -= delta;
$("time").textContent = (timeleft / 1000).toFixed(1);
if(timeleft <= 0){    
$("safeurldecodetime").style.display = "none";
var safeurldecodebutton = "<button onclick='safeurldecode()' id='safeurldecodebuttonpassword'><span class='fa fa-link'></span> Visit Link</button>";
var safeurldecodebuttonpassword = "<input type='text' placeholder='&#xf13e; Password' id='safeurldecodepassword'/><button  onclick='safeurldecode()' id='safeurldecodebuttonpassword'><span class='fa fa-lock'></span> Unlock Link</button>";
if ($("checkpassword").innerHTML == 0) {
$("safeurldecode").innerHTML = safeurldecodebuttonpassword;
}else {
$("safeurldecode").innerHTML = safeurldecodebutton;      
}    
clearInterval(downloadTimer);
}
},delta);
}

if (typeof adblocksettingplatinum !== 'undefined') {		
blockAdBlock.onDetected(function(){	
$("safeurldecodetime").innerHTML = "Disable Adblock !"; 
})
blockAdBlock.onNotDetected(function(){	
timeloader();
})
}else {
timeloader();
}

